import React, { useState } from "react";
import "../../styles/ViewCourses.css";
import Navbar from "../../components/Navbar"; // âœ… imported instead of rewriting
import { useNavigate } from "react-router-dom";

const ViewCourses = () => {
  const navigate = useNavigate();
  const [activeDropdown, setActiveDropdown] = useState("course");

  // Mock data (replace later with API)
  const [courses] = useState([
    {
      id: 1,
      title: "Introduction to Programming",
      category: "Programming",
      duration: "6 Weeks",
      description: "Learn the basics of programming using Python.",
    },
    {
      id: 2,
      title: "UI/UX Design Fundamentals",
      category: "Design",
      duration: "4 Weeks",
      description: "Master design principles and build modern interfaces.",
    },
    {
      id: 3,
      title: "Digital Marketing 101",
      category: "Marketing",
      duration: "5 Weeks",
      description: "Understand marketing strategies and social media tools.",
    },
  ]);

  const toggleDropdown = (menu) => {
    setActiveDropdown(activeDropdown === menu ? null : menu);
  };

  return (
    <div className="teacher-dashboard-layout">
      {/* ===== NAVBAR IMPORTED ===== */}
      <Navbar />

      {/* ===== BODY ===== */}
      <div className="teacher-body">
        {/* SIDEBAR */}
        <aside className="teacher-sidebar">
          <button onClick={() => navigate("/teacher-dashboard")}>
            <i className="fa-solid fa-house"></i> Dashboard
          </button>

          {/* COURSES DROPDOWN */}
          <div className="dropdown">
            <button
              className={activeDropdown === "course" ? "active" : ""}
              onClick={() => toggleDropdown("course")}
            >
              <i className="fa-solid fa-book"></i> Courses
            </button>
            {activeDropdown === "course" && (
              <div className="dropdown-menu">
                <ul>
                  <li onClick={() => navigate("/create-course")}>Create Course</li>
                  <li className="active" onClick={() => navigate("/view-courses")}>
                    View Courses
                  </li>
                  <li>Update Course</li>
                  <li>Delete Course</li>
                </ul>
              </div>
            )}
          </div>

          {/* MCQ DROPDOWN */}
          <div className="dropdown">
            <button
              className={activeDropdown === "mcq" ? "active" : ""}
              onClick={() => toggleDropdown("mcq")}
            >
              <i className="fa-solid fa-list-check"></i> MCQ Management
            </button>
            {activeDropdown === "mcq" && (
              <div className="dropdown-menu">
                <ul>
                  <li>Create MCQ</li>
                  <li>Update MCQ</li>
                </ul>
              </div>
            )}
          </div>

          <button>
            <i className="fa-solid fa-briefcase"></i> Case Studies
          </button>

          <button>
            <i className="fa-solid fa-chart-line"></i> Reports
          </button>
        </aside>

        {/* MAIN CONTENT */}
        <main className="teacher-content">
          <div className="view-courses">
            <h2>ðŸ“š All Courses</h2>
            <p>Here are all the courses youâ€™ve posted.</p>

            <div className="course-grid">
              {courses.map((course) => (
                <div key={course.id} className="course-card">
                  <h3>{course.title}</h3>
                  <p className="course-category">
                    <i className="fa-solid fa-tag"></i> {course.category}
                  </p>
                  <p className="course-desc">{course.description}</p>
                  <p className="course-duration">
                    <i className="fa-regular fa-clock"></i> {course.duration}
                  </p>
                  <div className="card-actions">
                    <button className="edit-btn">
                      <i className="fa-solid fa-pen"></i> Edit
                    </button>
                    <button className="delete-btn">
                      <i className="fa-solid fa-trash"></i> Delete
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </main>
      </div>
    </div>
  );
};

export default ViewCourses;
