import React, { useState, useEffect } from "react";
import "../../styles/CreateCourse.css";
import logo from "../../assets/logo.png";
import { useNavigate } from "react-router-dom";

const CreateCourse = () => {
  const navigate = useNavigate();
  const [menuOpen, setMenuOpen] = useState(false);
  const [activeDropdown, setActiveDropdown] = useState("course");

  const [formData, setFormData] = useState({
    title: "",
    description: "",
    category: "",
    duration: "",
  });

  // Handle dropdowns and profile menu
  const handleProfileClick = () => setMenuOpen(!menuOpen);

  useEffect(() => {
    const handleOutsideClick = (e) => {
      if (!e.target.closest(".profile-container")) setMenuOpen(false);
    };
    document.addEventListener("click", handleOutsideClick);
    return () => document.removeEventListener("click", handleOutsideClick);
  }, []);

  const toggleDropdown = (menu) => {
    setActiveDropdown(activeDropdown === menu ? null : menu);
  };

  const handleLogout = () => {
    localStorage.removeItem("teacher");
    navigate("/");
  };

  const handleChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Course Created:", formData);
    alert("‚úÖ Course created successfully!");
    setFormData({ title: "", description: "", category: "", duration: "" });
  };

  return (
    <div className="teacher-dashboard-layout">
      {/* ===== NAVBAR ===== */}
      <nav className="teacher-navbar">
        <div className="nav-left">
          <img src={logo} alt="BlueSheep" className="nav-logo" />
          <h2>BlueSheep University</h2>
        </div>

        <div className="nav-right">
          <span className="welcome-text">Welcome, Teacher üçé</span>

          <div className="notification-icon">
            <i className="fa-regular fa-bell"></i>
            <span className="notif-dot"></span>
          </div>

          <div className="profile-container">
            <div className="profile-circle" onClick={handleProfileClick}>
              <i className="fa-regular fa-user"></i>
            </div>

            {menuOpen && (
              <div className="profile-menu">
                <ul>
                  <li>
                    <i className="fa-regular fa-user"></i> My Profile
                  </li>
                  <li>
                    <i className="fa-solid fa-lock"></i> Change Password
                  </li>
                  <li onClick={handleLogout}>
                    <i className="fa-solid fa-right-from-bracket"></i> Logout
                  </li>
                </ul>
              </div>
            )}
          </div>
        </div>
      </nav>

      {/* ===== BODY ===== */}
      <div className="teacher-body">
        {/* SIDEBAR */}
        <aside className="teacher-sidebar">
          <button onClick={() => navigate("/teacher-dashboard")}>
            <i className="fa-solid fa-house"></i> Dashboard
          </button>

          {/* COURSES */}
          <div className="dropdown">
            <button
              className={activeDropdown === "course" ? "active" : ""}
              onClick={() => toggleDropdown("course")}
            >
              <i className="fa-solid fa-book"></i> Courses
            </button>
            {activeDropdown === "course" && (
              <div className="dropdown-menu">
                <ul>
                  <li className="active">Create Course</li>
                  <li>View Courses</li>
                  <li>Update Course</li>
                  <li>Delete Course</li>
                </ul>
              </div>
            )}
          </div>

          {/* MCQ */}
          <div className="dropdown">
            <button
              className={activeDropdown === "mcq" ? "active" : ""}
              onClick={() => toggleDropdown("mcq")}
            >
              <i className="fa-solid fa-list-check"></i> MCQ Management
            </button>
            {activeDropdown === "mcq" && (
              <div className="dropdown-menu">
                <ul>
                  <li>Create MCQ</li>
                  <li>Update MCQ</li>
                </ul>
              </div>
            )}
          </div>

          <button>
            <i className="fa-solid fa-briefcase"></i> Case Studies
          </button>

          <button>
            <i className="fa-solid fa-chart-line"></i> Reports
          </button>
        </aside>

        {/* MAIN CONTENT */}
        <main className="teacher-content">
          <div className="create-course-form">
            <h2>üìò Create a New Course</h2>
            <p>Fill out the form below to add a new course.</p>

            <form onSubmit={handleSubmit}>
              <label>Course Title</label>
              <input
                type="text"
                name="title"
                value={formData.title}
                onChange={handleChange}
                placeholder="Enter course title"
                required
              />

              <label>Description</label>
              <textarea
                name="description"
                value={formData.description}
                onChange={handleChange}
                placeholder="Enter course description"
                required
              ></textarea>

              <label>Category</label>
              <select
                name="category"
                value={formData.category}
                onChange={handleChange}
                required
              >
                <option value="">Select Category</option>
                <option value="Programming">Programming</option>
                <option value="Design">Design</option>
                <option value="Marketing">Marketing</option>
                <option value="Business">Business</option>
              </select>

              <label>Duration (weeks)</label>
              <input
                type="number"
                name="duration"
                value={formData.duration}
                onChange={handleChange}
                placeholder="Enter course duration"
                required
              />

              <button type="submit" className="submit-btn">
                Create Course
              </button>
            </form>
          </div>
        </main>
      </div>
    </div>
  );
};

export default CreateCourse;
