import React, { useState } from "react";
import "../../styles/CourseDetail.css";
import Navbar from "../../components/Navbar";
import { useParams, useNavigate } from "react-router-dom";

const CourseDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [activeDropdown, setActiveDropdown] = useState("course");
  const [showLessonPopup, setShowLessonPopup] = useState(false);
  const [showMcqPopup, setShowMcqPopup] = useState(false);

  const course = {
    id,
    title: "Introduction to Programming",
    description: "Learn the fundamentals of Python programming.",
    duration: "6 Weeks",
    category: "Programming",
  };

  const [lesson, setLesson] = useState({
    name: "",
    description: "",
    file: null,
  });

  const [mcq, setMcq] = useState({
    question: "",
    options: ["", "", "", ""],
    correct: "",
  });

  const toggleDropdown = (menu) => {
    setActiveDropdown(activeDropdown === menu ? null : menu);
  };

  const handleLessonChange = (e) => {
    const { name, value, files } = e.target;
    setLesson({ ...lesson, [name]: files ? files[0] : value });
  };

  const handleMcqChange = (e, index) => {
    const updated = [...mcq.options];
    updated[index] = e.target.value;
    setMcq({ ...mcq, options: updated });
  };

  const submitLesson = (e) => {
    e.preventDefault();
    alert(
      `âœ… Lesson Uploaded:\nName: ${lesson.name}\nDescription: ${lesson.description}\nFile: ${lesson.file?.name}`
    );
    setLesson({ name: "", description: "", file: null });
    setShowLessonPopup(false);
  };

  const submitMcq = (e) => {
    e.preventDefault();
    alert("âœ… MCQ posted successfully!");
    setShowMcqPopup(false);
  };

  return (
    <div className="teacher-dashboard-layout">
      {/* ===== NAVBAR ===== */}
      <Navbar />

      <div className="teacher-body">
        {/* ===== SIDEBAR ===== */}
        <aside className="teacher-sidebar">
          <button onClick={() => navigate("/teacher-dashboard")}>
            <i className="fa-solid fa-house"></i> Dashboard
          </button>

          {/* COURSES DROPDOWN */}
          <div className="dropdown">
            <button
              className={activeDropdown === "course" ? "active" : ""}
              onClick={() => toggleDropdown("course")}
            >
              <i className="fa-solid fa-book"></i> Courses
            </button>
            {activeDropdown === "course" && (
              <div className="dropdown-menu">
                <ul>
                  <li onClick={() => navigate("/create-course")}>
                    Create Course
                  </li>
                  <li className="active" onClick={() => navigate("/view-courses")}>
                    View Courses
                  </li>
                </ul>
              </div>
            )}
          </div>

          {/* MCQ MANAGEMENT DROPDOWN */}
          <div className="dropdown">
            <button
              className={activeDropdown === "mcq" ? "active" : ""}
              onClick={() => toggleDropdown("mcq")}
            >
              <i className="fa-solid fa-list-check"></i> MCQ Management
            </button>
            {activeDropdown === "mcq" && (
              <div className="dropdown-menu">
                <ul>
                  <li>Create MCQ</li>
                  <li>Update MCQ</li>
                </ul>
              </div>
            )}
          </div>

          <button>
            <i className="fa-solid fa-briefcase"></i> Case Studies
          </button>

          <button>
            <i className="fa-solid fa-chart-line"></i> Reports
          </button>
        </aside>

        {/* ===== MAIN CONTENT ===== */}
        <main className="teacher-content">
          <div className="course-detail">
            <h2>{course.title}</h2>
            <p>{course.description}</p>
            <p>
              <strong>Duration:</strong> {course.duration} |{" "}
              <strong>Category:</strong> {course.category}
            </p>

            <div className="course-actions">
              <button
                className="primary-btn"
                onClick={() => setShowLessonPopup(true)}
              >
                Upload Lesson
              </button>
              <button
                className="secondary-btn"
                onClick={() => setShowMcqPopup(true)}
              >
                Post MCQ
              </button>
            </div>

            {/* ===== LESSON UPLOAD POPUP ===== */}
            {showLessonPopup && (
              <div className="popup-overlay">
                <div className="popup">
                  <h3>ðŸ“˜ Upload Lesson</h3>
                  <form onSubmit={submitLesson}>
                    <label>Lesson Name</label>
                    <input
                      type="text"
                      name="name"
                      value={lesson.name}
                      onChange={handleLessonChange}
                      placeholder="Enter lesson name"
                      required
                    />

                    <label>Description</label>
                    <textarea
                      name="description"
                      value={lesson.description}
                      onChange={handleLessonChange}
                      placeholder="Enter lesson description"
                      required
                    ></textarea>

                    <label>Upload File</label>
                    <input
                      type="file"
                      name="file"
                      accept=".pdf,.docx,.mp4,.avi"
                      onChange={handleLessonChange}
                      required
                    />

                    <button type="submit" className="submit-btn">
                      Upload
                    </button>
                    <button
                      type="button"
                      className="cancel-btn"
                      onClick={() => setShowLessonPopup(false)}
                    >
                      Cancel
                    </button>
                  </form>
                </div>
              </div>
            )}

            {/* ===== MCQ POPUP ===== */}
            {showMcqPopup && (
              <div className="popup-overlay">
                <div className="popup">
                  <h3>ðŸ§© Post MCQ Question</h3>
                  <form onSubmit={submitMcq}>
                    <label>Question</label>
                    <input
                      type="text"
                      placeholder="Enter question"
                      value={mcq.question}
                      onChange={(e) =>
                        setMcq({ ...mcq, question: e.target.value })
                      }
                      required
                    />

                    {mcq.options.map((opt, i) => (
                      <input
                        key={i}
                        type="text"
                        placeholder={`Option ${i + 1}`}
                        value={opt}
                        onChange={(e) => handleMcqChange(e, i)}
                        required
                      />
                    ))}

                    <label>Correct Answer</label>
                    <input
                      type="text"
                      placeholder="Enter correct answer"
                      value={mcq.correct}
                      onChange={(e) =>
                        setMcq({ ...mcq, correct: e.target.value })
                      }
                      required
                    />

                    <button type="submit" className="submit-btn">
                      Post Question
                    </button>
                    <button
                      type="button"
                      className="cancel-btn"
                      onClick={() => setShowMcqPopup(false)}
                    >
                      Cancel
                    </button>
                  </form>
                </div>
              </div>
            )}
          </div>
        </main>
      </div>
    </div>
  );
};

export default CourseDetail;
