import React, { useState } from "react";
import "../../styles/admin.css";
import logo from "../../assets/logo.png";

const AdminDashboard = () => {
  const [activeSection, setActiveSection] = useState("dashboard");
  const [sidebarOpen, setSidebarOpen] = useState(true);

  const sections = [
    { id: "dashboard", name: "Dashboard", icon: "üìä" },
    { id: "user", name: "User Management", icon: "üë©‚Äçüéì" },
    { id: "teacher", name: "Teacher Management", icon: "üë®‚Äçüè´" },
    { id: "course", name: "Course Management", icon: "üìö" },
    { id: "quiz", name: "Quiz Management", icon: "üß©" },
    { id: "case", name: "Case Studies", icon: "üß†" },
    { id: "analytics", name: "Analytics & Reports", icon: "üìà" },
    { id: "settings", name: "Settings", icon: "‚öôÔ∏è" },
  ];

  return (
    <div className={`admin-layout ${sidebarOpen ? "sidebar-open" : "sidebar-closed"}`}>
      {/* ===== TOP NAVBAR ===== */}
      {/* <header className="admin-topbar">
        <div className="topbar-left">
          <button
            className="menu-toggle"
            onClick={() => setSidebarOpen(!sidebarOpen)}
          >
            ‚ò∞
          </button>
          <img src={logo} alt="Logo" />
          <h2>BlueSheep Admin Panel</h2>
        </div>
        <div className="topbar-right">
          <span className="admin-welcome">Welcome, Admin üëã</span>
          <button className="logout-btn">Logout</button>
        </div>
      </header> */}

      {/* ===== SIDEBAR + MAIN CONTENT ===== */}
      <div className="admin-container">
        {/* SIDEBAR */}
        <aside className={`admin-sidebar ${sidebarOpen ? "show" : "hide"}`}>
          <ul className="sidebar-menu">
            {sections.map((sec) => (
              <li
                key={sec.id}
                className={`menu-item ${
                  activeSection === sec.id ? "active" : ""
                }`}
                onClick={() => setActiveSection(sec.id)}
              >
                <span className="icon">{sec.icon}</span> {sec.name}
              </li>
            ))}
          </ul>
        </aside>

        {/* MAIN CONTENT */}
        <main className="admin-main">
          <h1 className="page-title">
            {sections.find((s) => s.id === activeSection)?.name}
          </h1>

          {activeSection === "dashboard" && (
            <div className="dashboard-overview">
              <div className="stat-card">üë©‚Äçüéì Total Students: 1280</div>
              <div className="stat-card">üë®‚Äçüè´ Total Teachers: 65</div>
              <div className="stat-card">üìö Active Courses: 42</div>
              <div className="stat-card">üß© Quizzes Available: 120</div>
            </div>
          )}

          {activeSection !== "dashboard" && (
            <div className="placeholder">
              <p>
                You are viewing{" "}
                <strong>
                  {sections.find((s) => s.id === activeSection)?.name}
                </strong>{" "}
                section.
              </p>
              <p>Feature development in progress...</p>
            </div>
          )}
        </main>
      </div>
    </div>
  );
};

export default AdminDashboard;
