import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import "../../styles/auth.css";

const Register = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    navigate("/student-dashboard"); // Simulated registration
  };

  return (
    <div className="auth-wrapper">
      {/* ===== LEFT SECTION ===== */}
      <div className="auth-left">
        <h1>Join <span>BlueSheep University</span></h1>
        <p>Sign up and start your journey to excellence today.</p>
        <img
          src="https://cdn-icons-png.flaticon.com/512/9044/9044123.png"
          alt="Register Illustration"
          className="auth-illustration"
        />
      </div>

      {/* ===== RIGHT SECTION ===== */}
      <div className="auth-right">
        <div className="auth-box">
          <h2>Create Account</h2>
          <p className="subtitle">Start learning with personalized courses.</p>

          <form onSubmit={handleSubmit}>
            <input
              type="text"
              placeholder="Full Name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
            <input
              type="email"
              placeholder="Email Address"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />

            <button type="submit" className="primary-btn">
              Create Account
            </button>
          </form>

          <p className="redirect-text">
            Already have an account?{" "}
            <Link to="/login" className="link">
              Login
            </Link>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Register;
