import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../../styles/TeacherDashboard.css";

const TeacherSidebar = ({ activeTab, setActiveTab }) => {
  const navigate = useNavigate();
  const [activeDropdown, setActiveDropdown] = useState(null);

  const toggleDropdown = (menu) => {
    setActiveDropdown(activeDropdown === menu ? null : menu);
  };

  return (
    <aside className="teacher-sidebar">
      {/* Dashboard */}
      <button
        className={activeTab === "dashboard" ? "active" : ""}
        onClick={() => setActiveTab("dashboard")}
      >
        <i className="fa-solid fa-house"></i> Dashboard
      </button>

      {/* ===== COURSES ===== */}
      <div className="dropdown">
        <button
          className={activeDropdown === "course" ? "active" : ""}
          onClick={() => toggleDropdown("course")}
        >
          <i className="fa-solid fa-book"></i> Courses
        </button>

        {activeDropdown === "course" && (
          <div className="dropdown-menu">
            <ul>
              <li onClick={() => navigate("/create-course")}>Create Course</li>
              <li onClick={() => navigate("/view-courses")}>View Courses</li>
            </ul>
          </div>
        )}
      </div>

      {/* ===== MCQ MANAGEMENT ===== */}
      <div className="dropdown">
        <button
          className={activeDropdown === "mcq" ? "active" : ""}
          onClick={() => toggleDropdown("mcq")}
        >
          <i className="fa-solid fa-list-check"></i> MCQ Management
        </button>

        {activeDropdown === "mcq" && (
          <div className="dropdown-menu">
            <ul>
              <li>Create MCQ</li>
              <li>Update MCQ</li>
            </ul>
          </div>
        )}
      </div>

      {/* ===== OTHER SECTIONS ===== */}
      <button
        className={activeTab === "case" ? "active" : ""}
        onClick={() => setActiveTab("case")}
      >
        <i className="fa-solid fa-briefcase"></i> Case Studies
      </button>

      <button
        className={activeTab === "reports" ? "active" : ""}
        onClick={() => setActiveTab("reports")}
      >
        <i className="fa-solid fa-chart-line"></i> Reports
      </button>
    </aside>
  );
};

export default TeacherSidebar;
