import React, { useState } from "react";
import "../../styles/CourseDetail.css";
import Navbar from "../../components/Navbar";
import { useParams } from "react-router-dom";

const CourseDetail = () => {
  const { id } = useParams();

  const [showLessonPopup, setShowLessonPopup] = useState(false);
  const [showMcqPopup, setShowMcqPopup] = useState(false);

  const course = {
    id,
    title: "Introduction to Programming",
    description: "Learn the fundamentals of Python programming.",
    duration: "6 Weeks",
    category: "Programming",
  };

  const [lesson, setLesson] = useState({
    name: "",
    description: "",
    file: null,
  });

  const [mcq, setMcq] = useState({
    question: "",
    options: ["", "", "", ""],
    correct: "",
  });

  const handleLessonChange = (e) => {
    const { name, value, files } = e.target;
    setLesson({ ...lesson, [name]: files ? files[0] : value });
  };

  const handleMcqChange = (e, index) => {
    const updated = [...mcq.options];
    updated[index] = e.target.value;
    setMcq({ ...mcq, options: updated });
  };

  const submitLesson = (e) => {
    e.preventDefault();
    alert(
      `âœ… Lesson Uploaded:\nName: ${lesson.name}\nDescription: ${lesson.description}\nFile: ${lesson.file?.name}`
    );
    setLesson({ name: "", description: "", file: null });
    setShowLessonPopup(false);
  };

  const submitMcq = (e) => {
    e.preventDefault();
    alert("âœ… MCQ posted successfully!");
    setShowMcqPopup(false);
  };

  return (
    <div className="teacher-dashboard-layout">
      <Navbar />

      <main className="course-detail">
        <h2>{course.title}</h2>
        <p>{course.description}</p>
        <p>
          <strong>Duration:</strong> {course.duration} |{" "}
          <strong>Category:</strong> {course.category}
        </p>

        <div className="course-actions">
          <button className="primary-btn" onClick={() => setShowLessonPopup(true)}>
            Upload Lesson
          </button>
          <button className="secondary-btn" onClick={() => setShowMcqPopup(true)}>
            Post MCQ
          </button>
        </div>

        {/* ===== Lesson Upload Modal ===== */}
        {showLessonPopup && (
          <div className="popup-overlay">
            <div className="popup">
              <h3>ðŸ“˜ Upload Lesson</h3>
              <form onSubmit={submitLesson}>
                <label>Lesson Name</label>
                <input
                  type="text"
                  name="name"
                  value={lesson.name}
                  onChange={handleLessonChange}
                  placeholder="Enter lesson name"
                  required
                />

                <label>Description</label>
                <textarea
                  name="description"
                  value={lesson.description}
                  onChange={handleLessonChange}
                  placeholder="Enter lesson description"
                  required
                ></textarea>

                <label>Upload File</label>
                <input
                  type="file"
                  name="file"
                  accept=".pdf,.docx,.mp4,.avi"
                  onChange={handleLessonChange}
                  required
                />

                <button type="submit" className="submit-btn">Upload</button>
                <button type="button" onClick={() => setShowLessonPopup(false)}>
                  Cancel
                </button>
              </form>
            </div>
          </div>
        )}

        {/* ===== MCQ Popup ===== */}
        {showMcqPopup && (
          <div className="popup-overlay">
            <div className="popup">
              <h3>ðŸ§© Post MCQ Question</h3>
              <form onSubmit={submitMcq}>
                <label>Question</label>
                <input
                  type="text"
                  placeholder="Enter Question"
                  value={mcq.question}
                  onChange={(e) => setMcq({ ...mcq, question: e.target.value })}
                  required
                />

                {mcq.options.map((opt, i) => (
                  <input
                    key={i}
                    type="text"
                    placeholder={`Option ${i + 1}`}
                    value={opt}
                    onChange={(e) => handleMcqChange(e, i)}
                    required
                  />
                ))}

                <label>Correct Answer</label>
                <input
                  type="text"
                  placeholder="Correct Answer"
                  value={mcq.correct}
                  onChange={(e) => setMcq({ ...mcq, correct: e.target.value })}
                  required
                />

                <button type="submit" className="submit-btn">Post Question</button>
                <button type="button" onClick={() => setShowMcqPopup(false)}>
                  Cancel
                </button>
              </form>
            </div>
          </div>
        )}
      </main>
    </div>
  );
};

export default CourseDetail;
