import React, { useState, useEffect } from "react";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";
import "../styles/landing.css";

// Carousel images
import ads1 from "../assets/images/ads1.jpeg";
import ads2 from "../assets/images/ads2.jpg";
import ads3 from "../assets/images/ads3.png";
// import hero from "../assets/images/hero.jpg";

const LandingPage = () => {
  const images = [ads1, ads2, ads3];
  const [currentIndex, setCurrentIndex] = useState(0);

  // Auto-slide every 4 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prev) => (prev + 1) % images.length);
    }, 4000);
    return () => clearInterval(interval);
  }, [images.length]);

  return (
    <>
      <Navbar />

      {/* ===== IMAGE CAROUSEL ===== */}
      <section className="carousel-section">
        <div className="carousel-container">
          {images.map((img, index) => (
            <div
              key={index}
              className={`carousel-slide ${index === currentIndex ? "active" : ""}`}
            >
              <img src={img} alt={`Advertisement ${index + 1}`} />
            </div>
          ))}
          <div className="carousel-dots">
            {images.map((_, index) => (
              <span
                key={index}
                className={`dot ${index === currentIndex ? "active" : ""}`}
                onClick={() => setCurrentIndex(index)}
              ></span>
            ))}
          </div>
        </div>
      </section>

      {/* ===== HERO SECTION ===== */}


      {/* ===== COURSE SECTION ===== */}
      <section className="courses-section">
        <h2>Industry-Relevant Free Courses</h2>
        <p>
          Explore our free online courses, designed by industry experts to boost your skills and advance your career growth.
        </p>

        <div className="courses-container">
          {/* Sidebar */}
          <div className="courses-sidebar">
            <button className="active-category">
              <i className="fa-solid fa-chart-line"></i> Popular Courses
            </button>
            <ul>
              <li><i className="fa-solid fa-building-columns"></i> University Programs</li>
              <li><i className="fa-brands fa-microsoft"></i> Microsoft Programs</li>
              <li><i className="fa-solid fa-briefcase"></i> Management</li>
              <li><i className="fa-solid fa-robot"></i> ChatGPT & Generative AI</li>
              <li><i className="fa-solid fa-brain"></i> Artificial Intelligence</li>
              <li><i className="fa-solid fa-gears"></i> Machine Learning</li>
              <li><i className="fa-solid fa-shield-halved"></i> Cyber Security</li>
            </ul>
          </div>

          {/* Main Courses Grid */}
          <div className="courses-main">
            <div className="subscription-banner">
              <div>
                <i className="fa-solid fa-crown"></i> Get unlimited certificates with an Academy Pro+ subscription
              </div>
              <div className="subscription-right">
                <p>$15/month</p>
                <button className="trial-btn">Start 7-Day Free Trial</button>
              </div>
            </div>

            <h3 className="section-subtitle">Popular Free Courses</h3>

            <div className="course-cards">
              {/* Card 1 */}
              <div className="course-card">
                <div className="course-img">
                  <span className="badge">FREE</span>
                  <img
                    src="https://www.simplilearn.com/ice9/course_images/leadership-and-management.webp"
                    alt="Leadership"
                  />
                </div>
                <div className="course-info">
                  <p className="rating">
                    ‚≠ê 4.61 ¬∑ 59.3K+ learners
                  </p>
                  <h4>Leadership and Management</h4>
                  <p className="duration">1.5 hrs</p>
                  <button className="view-btn">View Course</button>
                </div>
              </div>

              {/* Card 2 */}
              <div className="course-card">
                <div className="course-img">
                  <span className="badge">FREE</span>
                  <img
                    src="https://miro.medium.com/v2/resize:fit:1358/1*rrTt6Dd3hLKtCjO6GexUdw.jpeg"
                    alt="Prompt Engineering"
                  />
                </div>
                <div className="course-info">
                  <p className="rating">‚≠ê 4.57 ¬∑ 152.8K+ learners</p>
                  <h4>Prompt Engineering for ChatGPT</h4>
                  <p className="duration">2 hrs</p>
                  <button className="view-btn">View Course</button>
                </div>
              </div>

              {/* Card 3 */}
              <div className="course-card">
                <div className="course-img">
                  <span className="badge">FREE</span>
                  <img
                    src="https://www.analytixlabs.co.in/blog/wp-content/uploads/2023/05/procurement-management.jpg"
                    alt="Procurement Management"
                  />
                </div>
                <div className="course-info">
                  <p className="rating">‚≠ê 4.7 ¬∑ 29.7K+ learners</p>
                  <h4>Procurement Management</h4>
                  <p className="duration">1 hr</p>
                  <button className="view-btn">View Course</button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* ===== CTA SECTION ===== */}
      <section className="cta-section">
        <h2>Start Your Learning Journey Today üöÄ</h2>
        <p>
          Join thousands of learners worldwide and gain access to quality education powered by technology.
        </p>
        <button className="primary-btn">Join Now</button>
      </section>

      <Footer />
    </>
  );
};

export default LandingPage;
