import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import logo from "../assets/logo.png";
import "../styles/navbar.css";

const Navbar = () => {
  const navigate = useNavigate();
  const [isScrolled, setIsScrolled] = useState(false);

  // Add scroll effect for shadow
  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 10) setIsScrolled(true);
      else setIsScrolled(false);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <header className={`navbar-wrapper ${isScrolled ? "scrolled" : ""}`}>
      <nav className="navbar">
        {/* ===== Left Section ===== */}
        <div className="nav-left">
          <div className="nav-logo" onClick={() => navigate("/")}>
            <img src={logo} alt="BlueSheep Logo" />
            <h2>BlueSheep University</h2>
          </div>

          <div className="nav-browse-group">
            <div className="nav-dropdown">
              <select>
                <option>BROWSE</option>
                <option>Courses</option>
                <option>Programs</option>
                <option>Resources</option>
              </select>
            </div>

            <div className="nav-search">
              <input type="text" placeholder="Search Courses" />
              <i className="fa fa-search"></i>
            </div>
          </div>
        </div>

        {/* ===== Right Section ===== */}
        <div className="nav-right">
          <div className="nav-item">
            <i className="fa fa-crown"></i> Academy Pro+
            <span className="new-tag">NEW</span>
          </div>

          <div className="nav-item">
            <i className="fa fa-university"></i> University Programs
          </div>

          <button className="login-btn" onClick={() => navigate("/login")}>
            Login / Sign Up
          </button>
        </div>
      </nav>
    </header>
  );
};

export default Navbar;
